import { useState } from "react"
import { useTaskContext } from "../hooks/useTaskContext"
import { useAuthContext } from '../hooks/useAuthContext'

const TaskForm = ({ section }) => {
    const { dispatch } = useTaskContext()
    const { user } = useAuthContext()

    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [deadline, setDeadline] = useState(null)
    const [error, setError] = useState(null)
    const [emptyFields, setEmptyFields] = useState([])

    // Get the modal
    var modal = document.getElementById("myModal");

    // Get the button that opens the modal
    var btn = document.getElementById("myBtn");

    // Get the <span> element that closes the modal
    var span = document.getElementsByClassName("close")[0];

    // When the user clicks on the button, open the modal
    btn.onclick = function() {
    modal.style.display = "block";
    }

    // When the user clicks on <span> (x), close the modal
    span.onclick = function() {
    modal.style.display = "none";
    }

    // When the user clicks anywhere outside of the modal, close it
    window.onclick = function(event) {
    if (event.target === modal) {
        modal.style.display = "none";
    }
    }

    const handleSubmit = async (e) => {
        e.preventDefault()

        if (!user) {
            setError('You must be logged in')
            return
        }

        const task = {title, description, deadline, isCompleted: false, sectionId: section._id}

        const response = await fetch('/api/tasks', {
            method: 'POST',
            body: JSON.stringify(task),
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${user.token}`
            }
        })

        const json = await response.json()

        if (!response.ok) {
            setError(json.error)
            setEmptyFields(json.emptyFields)
        }
        if (response.ok) {
            setTitle('')
            setDescription('')
            setDeadline(null)
            setError(null)
            setEmptyFields([])
            dispatch({type: 'CREATE_TASK', payload: json})
            console.log("new task added", json)
        }
    }

    return (
        <form className="create" onSubmit={handleSubmit}>
            <h3>Add a new Task</h3>
            <div className="task-detail-box">
                <input
                    type="text"
                    onChange={(e) => setTitle(e.target.value)}
                    value={title}
                    className={emptyFields.includes('title') ? 'error' : 'task-detail-form'}
                    placeholder="Task name"
                />

                <input
                    type="text"
                    onChange={(e) => setDescription(e.target.value)}
                    value={description}
                    placeholder="Description"
                    className="task-detail-form"
                />

                <label className="task-detail-deadline">Deadline: </label>
                <input
                    type="date"
                    onChange={(e) => setDeadline(e.target.value)}
                    value={deadline}
                    className="task-detail-form"
                />
            </div>

            <button>Add Task</button>
            {error && <div className='error'>{error}</div>}
        </form>
    )
}

export default TaskForm
