import React, { useState, useEffect, useContext } from 'react';
import { useTaskContext } from "../hooks/useTaskContext"
import { useSectionContext } from "../hooks/useSectionContext"
import { useAuthContext } from "../hooks/useAuthContext";

import TaskDetails from "../components/TaskDetails"
import TaskFormBtn from "../components/TaskFormBtn"

const Section = ({ section, tasks }) => {
    //const { taskDispatch } = useTaskContext();
    //const { sectionDispatch } = useSectionContext();

    const taskContext = useTaskContext()
    const { user } = useAuthContext()
    const [sortBy, setSortBy] = useState('createdAt');
    const [sortOrder, setSortOrder] = useState('asc');

    useEffect(() => {
        const fetchTasks = async () => {
          const response = await fetch(`/api/tasks?sortBy=${sortBy}&sortOrder=${sortOrder}`, {
            headers: {'Authorization': `Bearer ${user.token}`},
          })
          
          const json = await response.json()
    
          if (response.ok) {
            taskContext.dispatch({type: 'SET_TASKS', payload: json})
          }
        }

    fetchTasks();
    }, [tasks, sortBy, sortOrder, section._id]);

    const handleSortChange = (field) => {
    if (sortBy === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('asc');
    }
  };

    return (
        <div className="task-section">
            <h1>{section.title}</h1>
            <div className="sorting-controls">
        <label>Sort by: </label>
        <select value={sortBy} onChange={handleSortChange}>
          <option value="createdAt">Creation Date</option>
          <option value="title">Title</option>
          <option value="description">Description</option>
          <option value="deadline">Deadline</option>
        </select>
        <button onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}>
          {sortOrder === 'asc' ? 'Ascending' : 'Descending'}
        </button>
      </div>
            <ul>
                {tasks && tasks.filter((task) => task.sectionId === section._id).map(task => (
                    <TaskDetails task={task} key={task._id} />
                ))}
            </ul>
            <TaskFormBtn section={section} />
        </div>
    )
}

export default Section
