import React, { useState, useEffect } from 'react';
import { useTaskContext } from "../hooks/useTaskContext"
import { useSectionContext } from "../hooks/useSectionContext"
import { useAuthContext } from "../hooks/useAuthContext";

import TaskDetails from "../components/TaskDetails"
import TaskFormBtn from "../components/TaskFormBtn"

const Section = ({ section, tasks}) => {
    //const { taskDispatch } = useTaskContext();
    //const { sectionDispatch } = useSectionContext();
    const { user } = useAuthContext()

    const [task, setTask] = useState([]);
    const [sortBy, setSortBy] = useState('createdAt'); // Default sort by createdAt
    const [sortOrder, setSortOrder] = useState('asc');

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await fetch(`/api/tasks?sortBy=${sortBy}&sortOrder=${sortOrder}`);
                const data = await response.json();
                setTask(data);
            } catch (error) {
            console.error('Error fetching tasks:', error);
            }
        };

        fetchTasks();
    }, [sortBy, sortOrder]);

  const handleSortChange = (field) => {
    if (sortBy === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('asc');
    }
  };


  return (
    <div>
      <div className="sorting-controls">
        <label>Sort by: </label>
        <select
          value={sortBy}
          onChange={(e) => handleSortChange(e.target.value)}
        >
          <option value="createdAt">Creation Date</option>
          <option value="title">Title</option>
          <option value="description">Description</option>
          <option value="deadline">Deadline</option>
        </select>
        <button onClick={() => handleSortChange(sortBy)}>
          {sortOrder === 'asc' ? 'Ascending' : 'Descending'}
        </button>
      </div>
      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            <h3>{task.title}</h3>
            <p>{task.description}</p>
            <p>{task.deadline}</p>
            <p>{new Date(task.createdAt).toLocaleDateString()}</p> {/* Display creation date */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Section
