import { useEffect, useState } from "react";
import { useGroupContext } from "../hooks/useGroupContext";
import { useAuthContext } from "../hooks/useAuthContext";

import GroupForm from "../components/GroupForm";

const Group = () => {
    const { groups, dispatch } = useGroupContext();
    const { user } = useAuthContext();

    const [sortBy, setSortBy] = useState('createdAt');
    const [sortOrder, setSortOrder] = useState('asc');

    useEffect(() => {
        const fetchGroups = async () => {
            if (!user) return;
    
            try {
                const response = await fetch(`/api/group?sortBy=${sortBy}&sortOrder=${sortOrder}`, {
                    headers: {'Authorization': `Bearer ${user.token}`},
                });
    
                const json = await response.json();
    
                if (response.ok) {
                    dispatch({ type: 'SET_GROUPS', payload: json });
                } else {
                    console.error("Error fetching groups:", json.message);
                }
            } catch (error) {
                console.error("Failed to fetch groups:", error);
            }
        };
        if(user){
            fetchGroups();
        }
    }, [user, dispatch]);

    return (
        <div>
            <GroupForm onGroupCreated={(newGroup) => dispatch({ type: 'CREATE_GROUP', payload: newGroup })} />
            <div className="groups-list">
                {groups ? (
                    groups.length > 0 ? (
                        groups.map((group) => (
                            <div key={group._id} className="group-item">
                                <h3>{group.name}</h3>
                                <h4>{group.members}</h4>
                            </div>
                        ))
                    ) : (
                        <p>No groups to display</p>
                    )
                ) : (
                    <p>Loading groups...</p>
                )}
            </div>
        </div>
    );
};

export default Group;
