import React, { useState, useEffect } from 'react';
import { useProfile } from '../hooks/useProfile';
import { useAuthContext } from '../hooks/useAuthContext';
const bcrypt = require('bcrypt')

const PasswordForm = ({ closePopup }) => {
    const [oldPassword, setOldPassword] = useState('')
    const [newPassword, setNewPassword] = useState('')
    const [comfirmPassword, setComfirmPassword] = useState('')
    const { changePassword } = useProfile()
    const { user } = useAuthContext()
    const [userData, setUserData] = useState('');

    useEffect(() => {
      const fetchUser = async () => {
        const response = await fetch(`/api/user/getUser/` + user._id, {
          headers: {'Authorization': `Bearer ${user.token}`},
        })
        
        const json = await response.json()
        console.log('user', json)
        setUserData(json[0]);
      }
      if(user){
        fetchUser()
      }
    }, [user])

    const handleSubmit = async (e) => {
        const match = await bcrypt.compare(oldPassword, userData.password)
        if (!match) {
          throw Error('Incorrect password')
        }
        await changePassword(newPassword)
        closePopup();
    }

    return (
    <div className="popup-form">
      <div className="popup-content">
        <span className="close-btn" onClick={closePopup}>&times;</span>
        <form className="create" onSubmit={handleSubmit}>
            <h3>Edit Profile</h3>
            <label>
              Old Password:
              <input type="text" name="oldPassword" onChange={(e) => setOldPassword(e.target.value)} />
            </label>
            <label>
              New Password:
              <input type="text" name="newPassword" onChange={(e) => setNewPassword(e.target.value)}/>
            </label>
            <label>
              Comfirm Password:
              <input type="text" name="comfirmPassword" onChange={(e) => setComfirmPassword(e.target.value)}/>
            </label>
            <button type="submit">Save Password</button>
        </form>
      </div>
    </div>
  );
}

export default PasswordForm;
