import React, { useState, useEffect } from 'react';
import { useProfile } from '../hooks/useProfile';
import { useLogin } from "../hooks/useLogin"
import { useAuthContext } from '../hooks/useAuthContext';

const ProfileForm = ({ closePopup }) => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [username, setUsername] = useState('')
    const {update, error, isLoading} = useProfile()
    const {login} = useLogin()
    const { user } = useAuthContext()

    useEffect(() => {
      const fetchUser = async () => {
        const response = await fetch(`/api/user/getUser/` + user._id, {
          headers: {'Authorization': `Bearer ${user.token}`},
        })
        
        const json = await response.json()
        console.log(json)
      }
      if(user){
        fetchUser()
      }
    }, [user. json])

    const handleSubmit = async (e) => {
        e.preventDefault()

        await update(json.email, username)
        await login(user)
        closePopup();
    }

    return (
    <div className="popup-form">
      <div className="popup-content">
        <span className="close-btn" onClick={closePopup}>&times;</span>
        <form className="create" onSubmit={handleSubmit}>
            <h3>Edit Profile</h3>
            <label>
              Username:
              <input type="text" name="username" defaultValue={user.username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <label>
              Display Name:
              <input type="text" name="displayname" defaultValue={user.displayname} />
            </label>
            <label>
              Email:
              <input type="text" name="email" defaultValue={user.email}/>
            </label>
            <label>
              Gender:
              <input type="text" name="gender" defaultValue={user.gender}/>
            </label>
            <button type="submit">Save Changes</button>
        </form>
      </div>
    </div>
  );
}

export default ProfileForm;
