import { useState } from 'react';
import TaskForm from './TaskForm';

const Main = ({ section }) => {
    const [showPopup, setShowPopup] = useState(false);

    const openPopup = () => {
        setShowPopup(true);
    };

    const closePopup = () => {
        setShowPopup(false);
    };

    return (
        <div>
            <button onClick={openPopup}>Open Form</button>
            {showPopup && <TaskForm section={section} closePopup={closePopup} />}
        </div>
    );
};

export default Main;
