import { useEffect, useState } from "react";
import { useAuthContext } from "../hooks/useAuthContext";

const Request = () => {
    const { user } = useAuthContext();
    const [error, setError] = useState(null);
    const [joinRequests, setJoinRequests] = useState([]);
    const [loading, setLoading] = useState(true);
    const [actionStatus, setActionStatus] = useState(null);

    useEffect(() => {
        const fetchRequests = async () => {
            if(!user){
                return
            }
            try {
                const response = await fetch(`/api/group/request`, {
                    headers: {'Authorization': `Bearer ${user.token}`},
                })
                const json = await response.json()
                if(!response.ok){
                    setError(json.message)
                } else {
                    setJoinRequests(json);
                }
            } catch (error) {
                setError("Failed to fetch join requests")
            } finally {
                setLoading(false)
            }
        }

        fetchRequests()
    }, [user])

    const handleApproveRequest = async (requestId, groupId) => {}
    const handleRejectRequest = async (requestId, groupId) => {}

    return (
        <div className="requests-column">
            <h3>Join Requests</h3>
            {loading && <p>Loading join requests...</p>}
            {error && <p className="error-message">{error}</p>}
            {joinRequests.length > 0 ? (
                joinRequests.map(request => (
                    <div key={request._id} className="request-item">
                        <p>Group: {request.name}</p>
                        <p>Members: {request.members}</p>
                        <button onClick={() => handleApproveRequest(request._id, request.groupId)}>Approve</button>
                        <button onClick={() => handleRejectRequest(request._id, request.groupId)}>Reject</button>
                    </div>
                ))
            ) : (
                !loading && <p>No join requests</p>
            )}
            {actionStatus && (
                <div className={`status-message ${actionStatus.success ? 'success' : 'error'}`}>
                    {actionStatus.message}
                </div>
            )}
        </div>
    )
}

export default Request;
