import React, { useState, useEffect } from 'react';
import { useProfile } from '../hooks/useProfile';
import { useAuthContext } from '../hooks/useAuthContext';

const PasswordForm = ({ closePopup }) => {
    const [currPassword, setcurrPassword] = useState('')
    const [newPassword, setNewPassword] = useState('')
    const [comfirmPassword, setComfirmPassword] = useState('')
    const { changePassword, error, isLoading } = useProfile()

    const handleSubmit = async (e) => {
        e.preventDefault()
        await changePassword(currPassword, newPassword, comfirmPassword)
    }

    return (
    <div className="popup-form">
      <div className="popup-content">
        <span className="close-btn" onClick={closePopup}>&times;</span>
        <form className="create" onSubmit={handleSubmit}>
            <h3>Edit Profile</h3>
            <label>
              Old Password:
              <input type="text" name="currPassword" onChange={(e) => setcurrPassword(e.target.value)} />
            </label>
            <label>
              New Password:
              <input type="text" name="newPassword" onChange={(e) => setNewPassword(e.target.value)}/>
            </label>
            <label>
              Comfirm Password:
              <input type="text" name="comfirmPassword" onChange={(e) => setComfirmPassword(e.target.value)}/>
            </label>
            <button type="submit">Save Password</button>
            {error && <div className='error'>{error}</div>}
        </form>
      </div>
    </div>
  );
}

export default PasswordForm;
