import React, { useState, useEffect } from 'react';
import { useProfile } from '../hooks/useProfile';
import { useLogin } from "../hooks/useLogin"
import { useAuthContext } from '../hooks/useAuthContext';

const ProfileForm = ({ closePopup }) => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [username, setUsername] = useState('')
    const {update, error, isLoading} = useProfile()
    const {login} = useLogin()
    const { user } = useAuthContext()

    const [formData, setFormData] = useState({
        username: '',
        displayName: '',
        email: '',
        gender: ''
      });
    
    useEffect(() => {
        if (user) {
            setFormData({
            username: user.username,
            displayName: user.displayname,
            email: user.email,
            gender: user.gender
            });
        }
    }, [user]);

    const handleSubmit = async (e) => {
        console.log('Form submitted with data:', formData);
        
        await update(email, password, formData.username)
        closePopup();
    }

    const handleChange = (e) => {
        setUsername(e.target.value)
    };

    return (
    <div className="popup-form">
      <div className="popup-content">
        <span className="close-btn" onClick={closePopup}>&times;</span>
        <form className="create" onSubmit={handleSubmit}>
            <h3>Edit Profile</h3>
            <label>
              Username:
              <input type="text" name="username" defaultValue={user.username} onChange={handleChange} />
            </label>
            <label>
              Display Name:
              <input type="text" name="displayname" defaultValue={user.displayname} onChange={handleChange}/>
            </label>
            <label>
              Email:
              <input type="text" name="email" defaultValue={user.email} onChange={handleChange}/>
            </label>
            <label>
              Gender:
              <input type="text" name="gender" defaultValue={user.gender} onChange={handleChange}/>
            </label>
            <button type="submit">Save Changes</button>
        </form>
      </div>
    </div>
  );
}

export default ProfileForm;
